/* --- Mapa base --- */
const spainMap = {
  viewBox: spainMapRaw.viewBox,
  locations: spainMapRaw.locations.map((l) => ({
    id: normalizeId(l.id ?? l.name),
    name: String(l.name),
    path: String(l.path),
  })),
};

/* --- Península --- */
const mainlandLocations = computed(() =>
  spainMap.locations.filter((l) => {
    const nid = normalizeId(l.id ?? l.name);
    const nname = normalizeId(l.name);
    return !/canar/.test(nid) && !/canar/.test(nname);
  })
);

/* --- Canarias, Ceuta, Melilla --- */
const canary =
  spainMap.locations.find((l) => /canar/.test(normalizeId(l.name))) || null;

const viewBox = (() => {
  const vb = parseViewBox(spainMap.viewBox);
  const centerOffsetX = vb.minX + vb.width / 2 - 430;
  const topOffset = 0;
  const heightCut = 200;
  return {
    minX: vb.minX - centerOffsetX,
    minY: vb.minY + topOffset,
    width: vb.width,
    height: vb.height - heightCut,
  };
})();

const insetRect = {
  x: viewBox.minX + viewBox.width * 0.66,
  y: viewBox.minY + viewBox.height * 0.75,
  width: viewBox.width * 0.15,
  height: viewBox.height * 0.15,
};
const canaryPath = ref(null);
const canaryTransform = ref("");

onMounted(() => {
  if (canaryPath.value) {
    const bbox = canaryPath.value.getBBox();
    const padding = 6;
    const scaleX = (insetRect.width - padding * 2) / bbox.width;
    const scaleY = (insetRect.height - padding * 2) / bbox.height;
    const scale = Math.min(scaleX, scaleY);
    const tx0 = -bbox.x;
    const ty0 = -bbox.y;
    const placedWidth = bbox.width * scale;
    const placedHeight = bbox.height * scale;
    const dx = insetRect.x + (insetRect.width - placedWidth) / 2;
    const dy = insetRect.y + (insetRect.height - placedHeight) / 2;
    canaryTransform.value = `translate(${dx},${dy}) scale(${scale}) translate(${tx0},${ty0})`;
  }
  // Compute a width that fits the longest label across both selects
  try {
    const candidates = [];
    candidates.push("Selecciona autonomía");
    candidates.push("Selecciona provincia");
    // CCAA labels
    for (const c of ccaaList) {
      const label = regionNamesEs[c.name] || c.name;
      candidates.push(String(label));
    }
    // Province labels (all provincias across all CCAA)
    for (const key of Object.keys(mapasCCAA)) {
      for (const p of mapasCCAA[key]) {
        candidates.push(String(p.nombre));
      }
    }
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    // Derive font from the first select
    const font = (ccaaSelectEl.value && window.getComputedStyle(ccaaSelectEl.value).font) || '14px system-ui';
    ctx.font = font;
    let max = 0;
    for (const text of candidates) {
      const w = ctx.measureText(text).width;
      if (w > max) max = w;
    }
    // padding-left/right (px-3 => ~12px each) + border + dropdown arrow space + small buffer
    const PADDING = 24; // 12px left + 12px right
    const BORDER = 2;   // 1px * 2
    const ARROW = 28;   // native select arrow
    const BUFFER = 8;   // safety
    const total = Math.ceil(max + PADDING + BORDER + ARROW + BUFFER);
    selectWidth.value = `${total}px`;
  } catch (e) {
    // Fallback width
    selectWidth.value = '280px';
  }
  // Mantener sincronizado con el store de auth
  try {
    watch(() => userLoggedIn().value, (v) => { isAuthed.value = !!v; });
    watch(() => userData().value, (ud) => { userId.value = ud?.id ?? null; });
  } catch (_) { }
});

const municipioOptions = computed(() =>
  (props.municipios || []).map((m) => ({ id: m.id, name: m.nombre }))
);
const localMunicipioId = computed({
  get: () => (props.municipioId != null ? String(props.municipioId) : ""),
  set: (v) => emit('update:municipioId', v),
});

/* --- Props y eventos --- */
const props = defineProps({
  modelValue: { type: String, default: null },
  municipios: { type: Array, default: () => [] },
  municipioId: { type: [String, Number], default: "" },
  suppressRestore: { type: Boolean, default: false },
});
const emit = defineEmits(["update:modelValue", "change", "province-change", "update:municipioId"]);
const internalValue = ref(props.modelValue ?? "");
const selectedProvince = ref("");
const ccaaSelectEl = ref(null);
const selectWidth = ref("auto");
const isAuthed = ref(false);
let saveTimer = null;
const userId = ref(null);
const programmaticProvinceSet = ref(false);
const provinceModalOpen = ref(false);
const saving = ref(false);
const selectedCodauto = computed(() => {
  const row = (cpList.value || []).find(
    (r) => String(r.cpro) === String(selectedProvince.value)
  );
  return row?.codauto || '';
});
const hasMunicipio = computed(() => {
  const v = localMunicipioId.value;
  return v !== null && v !== undefined && String(v) !== '';
});
const canSave = computed(() => {
  return Boolean(
    (internalValue.value && String(internalValue.value) !== '') &&
    (selectedProvince.value && String(selectedProvince.value) !== '') &&
    (selectedAreaCode.value && String(selectedAreaCode.value) !== '') &&
    hasMunicipio.value
  );
});

function nsKey(base) {
  return isAuthed.value && userId.value ? `locpref_${userId.value}_${base}` : `locpref_${base}`;
}
function lsGet(base) {
  try { return localStorage.getItem(nsKey(base)); } catch { return null; }
}
function lsSet(base, val) {
  try { localStorage.setItem(nsKey(base), String(val ?? '')); } catch { }
}
function lsRemoveLegacyGuestKeys() {
  // Remove non-namespaced keys to avoid cross-user leakage
  try {
    ['ccaa', 'cpro', 'area'].forEach(k => localStorage.removeItem(`locpref_${k}`));
  } catch { }
}

// Datos de comunidades_provincias y área montañosa
const cpList = ref([]);
const selectedAreaCode = ref("");
const montanosasOptions = computed(() => {
  const map = new Map();
  for (const r of cpList.value) {
    if (r?.codAreaMont && r?.areaMontana && !map.has(r.codAreaMont)) {
      map.set(r.codAreaMont, r.areaMontana);
    }
  }
  return Array.from(map.entries()).map(([code, name]) => ({ code, name }));
});

onMounted(async () => {
  // Cargar catálogo comunidades_provincias
  try {
    const res = await axiosClient.get("comunidades-provincias");
    cpList.value = Array.isArray(res.data) ? res.data : [];
  } catch (e) {
    console.error("[MapaEspana] Error cargando comunidades_provincias", e);
    cpList.value = [];
  }

  // Detectar autenticación siempre (independiente de suppressRestore)
  let authed = false;
  // Inicializar desde el store (mismo origen que GlassNav)
  try {
    const logged = userLoggedIn().value;
    const ud = userData().value;
    if (logged) {
      isAuthed.value = true;
      userId.value = ud?.id ?? null;
    }
  } catch (_) { }
  try {
    const me = await axiosClient.get('me');
    if (me && me.status === 200) {
      authed = true;
      isAuthed.value = true;
      userId.value = me?.data?.id ?? me?.data?.user?.id ?? null;
      lsRemoveLegacyGuestKeys();
      // Restaurar solo si no se suprime
      if (!props.suppressRestore) {
        try {
          const pref = await axiosClient.get('user/location-pref');
          const data = pref?.data || {};
          if (data.ccaa_id) {
            internalValue.value = String(data.ccaa_id);
            emit("update:modelValue", internalValue.value);
            emit("change", internalValue.value);
          }
          if (data.cpro) {
            setTimeout(() => {
              const provs = selectedProvinces.value || [];
              if (provs.some(p => String(p.cpro) === String(data.cpro))) {
                programmaticProvinceSet.value = true;
                selectedProvince.value = String(data.cpro);
                setTimeout(() => { programmaticProvinceSet.value = false; });
              }
            });
          }
          if (data.area_code) {
            selectedAreaCode.value = String(data.area_code);
          }
        } catch (_) { }
      }
    }
  } catch (_) { }

  // Invitado: restaurar desde localStorage solo si no se suprime
  if (!authed && !props.suppressRestore) {
    try {
      const savedCcaa = lsGet('ccaa') || '';
      const savedCpro = lsGet('cpro') || '';
      const savedArea = lsGet('area') || '';
      if (savedCcaa) {
        internalValue.value = savedCcaa;
        emit("update:modelValue", savedCcaa);
        emit("change", savedCcaa);
        if (savedCpro) {
          setTimeout(() => {
            const provs = selectedProvinces.value || [];
            if (provs.some(p => String(p.cpro) === String(savedCpro))) {
              programmaticProvinceSet.value = true;
              selectedProvince.value = savedCpro;
              setTimeout(() => { programmaticProvinceSet.value = false; });
            }
          });
        }
      }
      if (savedArea) {
        selectedAreaCode.value = savedArea;
      }
    } catch { }
  }

  // Si se suprime restauración, asegurar estado vacío visible
  if (props.suppressRestore) {
    internalValue.value = "";
    selectedProvince.value = "";
    selectedAreaCode.value = "";
    emit("update:modelValue", "");
    emit("change", "");
    emit('update:municipioId', "");
  }
});

watch(
  () => props.modelValue,
  (v) => (internalValue.value = v)
);

// Reset province whenever autonomy changes (including via select UI)
// Removed reset of selectedProvince on CCAA change to allow auto-select of single-province CCAA

watch(
  () => selectedProvince.value,
  (cpro) => {
    if (!cpro) return;
    const prov = selectedProvinces.value.find((p) => p.cpro === cpro);
    const name = prov ? prov.nombre : "";
    emit("province-change", { cpro, name, auto: programmaticProvinceSet.value });
    // Autocompletar área montañosa según tabla comunidades_provincias
    const match = cpList.value.find((r) => String(r.cpro) === String(cpro));
    selectedAreaCode.value = match?.codAreaMont || "";
    // Guardar provincia
    lsSet('cpro', cpro);
    // Guardado en BD (autenticado)
    savePrefDebounced();
  }
);

/* --- Lista CCAA --- */
const ccaaList = Object.keys(mapasCCAA)
  .map((k) => ({ id: normalizeId(k), name: k }))
  .concat([
    { id: "ceuta", name: "Ceuta" },
    { id: "melilla", name: "Melilla" },
  ])
  .sort((a, b) => (regionNamesEs[a.name] || a.name).localeCompare(regionNamesEs[b.name] || b.name, "es"));

/* --- Provincias de la CCAA seleccionada --- */
const selectedProvinces = computed(() => {
  if (!internalValue.value) return [];
  const ccaaKey = Object.keys(mapasCCAA).find(
    (ccaa) => normalizeId(ccaa) === internalValue.value
  );
  return ccaaKey ? mapasCCAA[ccaaKey] : [];
});

const selectedName = computed(() => {
  const c = ccaaList.find((c) => c.id === internalValue.value);
  return c ? regionNamesEs[c.name] || c.name : "";
});

const selectedProvinceName = computed(() => {
  const prov = selectedProvinces.value.find(
    (p) => p.cpro === selectedProvince.value
  );
  return prov ? prov.nombre : "";
});

function select(id) {
  internalValue.value = id;
  selectedProvince.value = ""; // Reset provincia al cambiar CCAA
  emit("update:modelValue", id);
  emit("change", id);
  // Limpiar municipio seleccionado al cambiar CCAA
  emit('update:municipioId', "");
  // Guardar CCAA
  lsSet('ccaa', id);
  // Auto-seleccionar la provincia si la CCAA solo tiene una
  const provs = selectedProvinces.value;
  if (Array.isArray(provs) && provs.length === 1) {
    selectProvince(provs[0].cpro);
  }
  // Guardado en BD (autenticado)
  savePrefDebounced();
}
function selectProvince(cpro) {
  selectedProvince.value = cpro;
  // Limpiar municipio seleccionado al cambiar provincia
  emit('update:municipioId', "");
}
async function onSelectChange() {
  emit("update:modelValue", internalValue.value);
  emit("change", internalValue.value);
  // Limpiar municipio seleccionado al cambiar CCAA desde desplegable
  emit('update:municipioId', "");
  // Guardar CCAA
  lsSet('ccaa', internalValue.value);
  // Auto-seleccionar la provincia si la CCAA solo tiene una
  await nextTick();
  const provs = selectedProvinces.value;
  if (Array.isArray(provs) && provs.length === 1) {
    selectProvince(provs[0].cpro);
  } else {
    // Si NO hay exactamente una provincia (0 o >1), abrir modal de provincias
    provinceModalOpen.value = true;
  }
  // Guardado en BD (autenticado)
  savePrefDebounced();
}
function sel(id) {
  return internalValue.value === id ? "selected" : "";
}
function selProvince(cpro) {
  return selectedProvince.value === cpro ? "selected" : "";
}

// Guardar área montañosa cuando cambie manualmente
watch(
  () => selectedAreaCode.value,
  (code) => {
    lsSet('area', code || '');
    savePrefDebounced();
  }
);

function onMapClick(event) {
  const target = event.target;
  if (!target) return;
  const path = target.closest && target.closest('path');
  if (!path) return;
  const provName = path.getAttribute('name');
  if (!provName) return;

  // Find the CCAA that contains this province
  let foundCcaaKey = null;
  let foundProvince = null;
  const toNorm = (s) =>
    String(s)
      .toLowerCase()
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '') // remove diacritics
      .replace(/ç/g, 'c')
      .trim();
  const aliases = {
    'gerona': 'girona',
    'lerida': 'lleida',
    'guipuzcoa': 'gipuzkoa',
    'vizcaya': 'bizkaia',
    'la coruna': 'a coruna',
    'orense': 'ourense',
  };
  const normName = (s) => {
    const n = toNorm(s);
    return aliases[n] || n;
  };
  const provNameLc = normName(provName);
  for (const ccaaKey of Object.keys(mapasCCAA)) {
    const prov = mapasCCAA[ccaaKey].find((p) => normName(p.nombre) === provNameLc);
    if (prov) {
      foundCcaaKey = ccaaKey;
      foundProvince = prov;
      break;
    }
  }

  if (foundCcaaKey) {
    const ccaaId = normalizeId(foundCcaaKey);
    // If CCAA changes, select() will reset province; set it after
    const cpro = foundProvince && foundProvince.cpro ? String(foundProvince.cpro) : "";
    const changed = internalValue.value !== ccaaId;
    select(ccaaId);
    if (cpro && !changed) {
      // Same CCAA clicked; just set province
      selectProvince(cpro);
    } else if (cpro && changed) {
      // After changing CCAA, set the province in next tick
      setTimeout(() => selectProvince(cpro));
    }
  } else {
    // If province not found, try clicking by matching a CCAA by name directly
    const ccaa = Object.keys(regionNamesEs).find(
      (k) => normName(regionNamesEs[k]) === provNameLc || normName(k) === provNameLc
    );
    if (ccaa) select(normalizeId(ccaa));
  }
}
</script>

